import React from "react";
import { Button, Form, FormGroup, Input, Label } from "reactstrap";

import axios from "axios";

import { API_URL } from "../constants";

class SymptomSelect extends React.Component {
  state = {
    value: "",
    symptoms: [],
    symptomSubmitted : false
  };

  componentDidMount() {
//    if (this.props.symptom){
//      const { sid, sname } = this.props.symptom;
//      this.setState({ sid, sname });
//    }
    this.getSymptoms();
  }

  getSymptoms = () => {
    axios.get(API_URL).then(res => this.setState({ symptoms: res.data }));
  };

  onChange = e => {
    this.setState({ [e.target.name]: e.target.value });
    this.setState({ value: e.target.value});
  };

  diagnose = e => {
    e.preventDefault();
    axios.get(API_URL + 'symptoms/' + e.target.name + '/diagnose', this.state).then(() => {
      this.setState({symptomSubmitted: true})
    });
  };

  render() {
    return (
      <div>
        <Form onSubmit={this.diagnose}>
          <FormGroup>
            <Label for="symptomSelect">Choose your symptom</Label>
            <Input type="select" name="symptom" id="symptomSelect">
              {this.state.symptoms.map((symptom) => <option key={symptom.id} value={symptom.id}>{symptom.name}</option>)}
            </Input>
          </FormGroup>
          <Button>Submit</Button>
        </Form>
      </div>


    );
  }
}

export default SymptomSelect;
