import React from "react";
import { Button, Form, FormGroup, Input, Label } from "reactstrap";

import axios from "axios";

import { API_URL } from "../constants";

class SymptomSelect extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: '',
      symptoms: [],
      symptomSubmitted : false
    };

    this.handleSubmit = this.handleSubmit.bind(this);
  }

  componentDidMount() {
    this.getSymptoms();
  }

  getSymptoms = () => {
    axios.get(API_URL).then(res => this.setState({ symptoms: res.data }));
  };

  onChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  handleSubmit(event) {
    event.preventDefault();
    this.setState({value: event.target.value});
    axios.get(API_URL + 'symptoms/' + symptom.value + '/diagnose', this.state).then(() => {
      this.setState({symptomSubmitted: true})
    });
  };

//  diagnose = e => {
//    e.preventDefault();
//    axios.get(API_URL + 'symptoms/' + this.state.value + '/diagnose', this.state).then(() => {
//      this.setState({symptomSubmitted: true})
//    });
//  };

  render() {
    return (
      <div>
        <Form onSubmit={this.handleSubmit}>
          <FormGroup>
            <Label for="symptomSelect">Choose your symptom</Label>
            <Input type="select" name="symptom" id="symptomSelect" value="this.state">
              {this.state.symptoms.map((symptom) => <option key={symptom.id} value={symptom.id}>{symptom.name}</option>)}
            </Input>
          </FormGroup>
          <Button>Submit</Button>
        </Form>
      </div>


    );
  }
}

export default SymptomSelect;
